package define

import (
	"flag"
	"fmt"
	"math/big"
)

const (
	VLogLevel = 10
)

var (
	LeadsExpireHour = flag.Int("leads_expire_hour", 720, "")
	Prime           = hexMust2BigInt("")
)

func hexMust2BigInt(hex string) *big.Int {
	bi, ok := big.NewInt(0).SetString(hex, 16)
	if !ok {
		panic(fmt.Errorf("not valid prime[%s]", hex))
	}
	return bi
}
